import { useEffect } from "react";
import { motion, useAnimation } from "framer-motion";
import { Button } from "@/components/ui/button";

export default function Home() {
  const controls = useAnimation();

  useEffect(() => {
    controls.start({ opacity: 1, y: 0 });
  }, [controls]);

  return (
    <main className="min-h-screen bg-[#FAF6F1] text-black">
      <section className="h-screen flex items-center justify-center">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={controls}
          transition={{ duration: 1 }}
          className="text-center"
        >
          <h1 className="text-5xl font-bold mb-4">Feel the Vibe</h1>
          <p className="text-lg mb-8">A modern experience in motion</p>
          <button className="bg-black text-white px-6 py-3 rounded-full text-lg hover:bg-opacity-80 transition-all">
          Enter
        </button>
        </motion.div>
      </section>

      <section className="min-h-screen flex items-center justify-center bg-white text-black">
        <motion.div
          initial={{ opacity: 0, scale: 0.95 }}
          whileInView={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.8 }}
          className="max-w-xl text-center"
        >
          <h2 className="text-4xl font-semibold mb-4">Smooth Interactions</h2>
          <p className="text-base">
            Inspired by the elegant aesthetic of Fit-Song.jp, this site embraces
            minimalism, subtle animations, and a gentle color palette.
          </p>
        </motion.div>
      </section>
    </main>
  );
}
