import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import quakeImg1 from '../assets/quake1.jpg';
import quakeImg2 from '../assets/quake2.jpg';
import quakeImg3 from '../assets/quake3.jpg';

gsap.registerPlugin(ScrollTrigger);

const sections = [
  {
    title: "Seismic Facts",
    image: quakeImg1,
    content: [
      "Earthquakes are caused by tectonic shifts.",
      "The Pacific Ring of Fire is the most active zone.",
      "Aftershocks can be just as dangerous as the main quake."
    ]
  },
  {
    title: "What To Do",
    image: quakeImg2,
    content: [
      "DROP to your hands and knees.",
      "COVER your head and neck.",
      "HOLD ON until the shaking stops."
    ]
  },
  {
    title: "Emergency Kit Essentials",
    image: quakeImg3,
    content: [
      "Water for at least 3 days.",
      "Non-perishable food.",
      "Flashlight and batteries.",
      "Whistle and first aid supplies."
    ]
  }
];

const FactSection = () => {
  const sectionRefs = useRef([]);

  useEffect(() => {
    sectionRefs.current.forEach((el) => {
      gsap.fromTo(
        el,
        { opacity: 0, y: 100 },
        {
          opacity: 1,
          y: 0,
          scrollTrigger: {
            trigger: el,
            start: 'top 85%',
            toggleActions: 'play none none reverse'
          },
          duration: 0.8,
          ease: 'power3.out'
        }
      );
    });
  }, []);

  return (
    <div className="scroll-sections">
      {sections.map((section, i) => (
        <section
          key={i}
          className="scroll-section"
          ref={(el) => (sectionRefs.current[i] = el)}
        >
          <h2>{section.title}</h2>
          <img src={section.image} alt={section.title} className="section-image" />
          <ul>
            {section.content.map((text, j) => (
              <li key={j}>{text}</li>
            ))}
          </ul>
        </section>
      ))}
    </div>
  );
};

export default FactSection;
