import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  // Authentication state
  const [user, setUser] = useState(null);
  const [authMode, setAuthMode] = useState(null); // 'signin' or 'signup'
  const [authForm, setAuthForm] = useState({
    email: '',
    password: '',
    confirmPassword: ''
  });

  // Navigation state
  const [activeTab, setActiveTab] = useState('preparedness');

  // Handle authentication
  const handleAuthSubmit = (e) => {
    e.preventDefault();
    if (authMode === 'signup' && authForm.password !== authForm.confirmPassword) {
      alert("Passwords don't match!");
      return;
    }
    // In a real app, you would verify credentials with backend
    setUser({
      email: authForm.email,
      name: "User Name" // This would come from your auth system
    });
    setAuthMode(null);
    setActiveTab('alerts'); // Switch to alerts after login
  };

  // Protected content based on auth status
  const renderContent = () => {
    if (!user) {
      return (
        <>
          {activeTab === 'preparedness' && <PreparednessTab />}
          {activeTab === 'resources' && <ResourcesTab />}
        </>
      );
    } else {
      return (
        <>
          {activeTab === 'alerts' && <AlertsTab />}
          {activeTab === 'map' && <MapTab />}
          {activeTab === 'preparedness' && <PreparednessTab />}
          {activeTab === 'resources' && <ResourcesTab />}
          {activeTab === 'sos' && <SosTab />}
        </>
      );
    }
  };

  return (
    <div className="e-quake-app">
      {/* Header with Auth Status */}
      <header className="app-header">
        <div className="header-left">
          <h1 className="app-title">E-QUAKE</h1>
          {user && (
            <div className="user-info">
              <span className="user-name">{user.name}</span>
              <span className="user-email">{user.email}</span>
            </div>
          )}
        </div>
        <div className="header-right">
          {user ? (
            <button className="auth-btn signout" onClick={() => setUser(null)}>
              Sign Out
            </button>
          ) : (
            <>
              <button className="auth-btn signin" onClick={() => setAuthMode('signin')}>
                Sign In
              </button>
              <button className="auth-btn signup" onClick={() => setAuthMode('signup')}>
                Sign Up
              </button>
            </>
          )}
        </div>
      </header>

      {/* Navigation Tabs */}
      <nav className="app-nav">
        {user && (
          <>
            <button 
              className={`nav-btn ${activeTab === 'alerts' ? 'active' : ''}`}
              onClick={() => setActiveTab('alerts')}
            >
              Alerts
            </button>
            <button 
              className={`nav-btn ${activeTab === 'map' ? 'active' : ''}`}
              onClick={() => setActiveTab('map')}
            >
              Map
            </button>
            <button 
              className={`nav-btn ${activeTab === 'sos' ? 'active' : ''}`}
              onClick={() => setActiveTab('sos')}
            >
              SOS
            </button>
          </>
        )}
        <button 
          className={`nav-btn ${activeTab === 'preparedness' ? 'active' : ''}`}
          onClick={() => setActiveTab('preparedness')}
        >
          Preparedness
        </button>
        <button 
          className={`nav-btn ${activeTab === 'resources' ? 'active' : ''}`}
          onClick={() => setActiveTab('resources')}
        >
          Resources
        </button>
      </nav>

      {/* Main Content */}
      <main className="app-content">
        {renderContent()}
      </main>

      {/* Authentication Modal */}
      {authMode && (
        <div className="auth-modal">
          <div className="auth-container">
            <button className="close-auth" onClick={() => setAuthMode(null)}>Ã—</button>
            <h2>{authMode === 'signin' ? 'Sign In' : 'Create Account'}</h2>

            <form onSubmit={handleAuthSubmit}>
              <div className="form-group">
                <label>Email</label>
                <input
                  type="email"
                  name="email"
                  value={authForm.email}
                  onChange={(e) => setAuthForm({...authForm, email: e.target.value})}
                  required
                />
              </div>

              <div className="form-group">
                <label>Password</label>
                <input
                  type="password"
                  name="password"
                  value={authForm.password}
                  onChange={(e) => setAuthForm({...authForm, password: e.target.value})}
                  required
                  minLength="6"
                />
              </div>

              {authMode === 'signup' && (
                <div className="form-group">
                  <label>Confirm Password</label>
                  <input
                    type="password"
                    name="confirmPassword"
                    value={authForm.confirmPassword}
                    onChange={(e) => setAuthForm({...authForm, confirmPassword: e.target.value})}
                    required
                    minLength="6"
                  />
                </div>
              )}

              <button type="submit" className="auth-submit">
                {authMode === 'signin' ? 'Sign In' : 'Sign Up'}
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

// Tab Components (simplified for example)
function AlertsTab() {
  return <div className="tab-content">Alerts Content</div>;
}

function MapTab() {
  return <div className="tab-content">Map Content</div>;
}

function SosTab() {
  return <div className="tab-content">SOS Emergency Features</div>;
}

function PreparednessTab() {
  return <div className="tab-content">Preparedness Guides</div>;
}

function ResourcesTab() {
  return <div className="tab-content">Educational Resources</div>;
}

export default App;
