import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [authMode, setAuthMode] = useState(null); // 'signin' or 'signup'
  const [authForm, setAuthForm] = useState({
    email: '',
    password: '',
    confirmPassword: ''
  });

  // Quiz questions pool
  const quizQuestionsPool = [
    {
      id: 'q1',
      question: "What should you do FIRST when an earthquake starts?",
      type: 'mcq',
      options: [
        { id: 'a', text: "Run outside to an open area", correct: false },
        { id: 'b', text: "Drop, cover, and hold on", correct: true },
        { id: 'c', text: "Stand in a doorway", correct: false }
      ],
      explanation: "The 'Drop, Cover, and Hold On' technique is recommended by safety experts worldwide."
    },
    {
      id: 'q2',
      question: "Which items should be in your emergency kit?",
      type: 'checkbox',
      options: [
        { id: 'a', text: "Water (1 gallon per person per day)", correct: true },
        { id: 'b', text: "Flashlight with extra batteries", correct: true },
        { id: 'c', text: "First aid kit", correct: true },
        { id: 'd', text: "All of the above", correct: true }
      ],
      explanation: "All these items are essential for an emergency kit."
    },
    {
      id: 'q3',
      question: "True or False: You should use elevators during an earthquake.",
      type: 'truefalse',
      correctAnswer: "false",
      explanation: "Never use elevators during an earthquake as power may fail."
    },
    {
      id: 'q4',
      question: "Where is the safest place to be during an earthquake if you're indoors?",
      type: 'mcq',
      options: [
        { id: 'a', text: "Under a sturdy table", correct: true },
        { id: 'b', text: "Next to a window", correct: false },
        { id: 'c', text: "In the basement", correct: false }
      ],
      explanation: "Under sturdy furniture provides protection from falling objects."
    },
    {
      id: 'q5',
      question: "How often should you practice earthquake drills with your family?",
      type: 'mcq',
      options: [
        { id: 'a', text: "Every month", correct: false },
        { id: 'b', text: "Every 6 months", correct: true },
        { id: 'c', text: "Once a year", correct: false }
      ],
      explanation: "Practicing every 6 months helps maintain preparedness."
    }
  ];

  // Randomly select 3 questions for the quiz
  const [currentQuizQuestions, setCurrentQuizQuestions] = useState([]);
  const [quizAnswers, setQuizAnswers] = useState({});
  const [quizSubmitted, setQuizSubmitted] = useState(false);

  const generateNewQuiz = () => {
    // Shuffle array and pick 3 questions
    const shuffled = [...quizQuestionsPool].sort(() => 0.5 - Math.random());
    setCurrentQuizQuestions(shuffled.slice(0, 3));
    setQuizAnswers({});
    setQuizSubmitted(false);
  };

  useEffect(() => {
    generateNewQuiz();
  }, []);

  // Authentication handlers
  const handleAuthInputChange = (e) => {
    const { name, value } = e.target;
    setAuthForm({
      ...authForm,
      [name]: value
    });
  };

  const handleSignUp = (e) => {
    e.preventDefault();
    if (authForm.password !== authForm.confirmPassword) {
      alert("Passwords don't match!");
      return;
    }
    // In a real app, you would call your backend here
    setUser({
      email: authForm.email
    });
    setAuthMode(null);
    alert("Account created successfully!");
  };

  const handleSignIn = (e) => {
    e.preventDefault();
    // In a real app, you would verify credentials with backend
    setUser({
      email: authForm.email
    });
    setAuthMode(null);
    alert("Signed in successfully!");
  };

  const handleSignOut = () => {
    setUser(null);
  };

  const [earthquakes, setEarthquakes] = useState([
    { id: 1, location: "Davao", magnitude: 7.6, depth: "35km", time: "2 hours ago", alert: "red" },
    { id: 2, location: "Manila", magnitude: 5.2, depth: "15km", time: "5 hours ago", alert: "yellow" },
    { id: 3, location: "Cebu", magnitude: 4.1, depth: "25km", time: "1 day ago", alert: "green" }
  ]);

  const [activeTab, setActiveTab] = useState('alerts');
  const [userLocation, setUserLocation] = useState(null);

  // Simulate fetching user location
  useEffect(() => {
    // In a real app, this would use the Geolocation API
    setUserLocation({ lat: 8.4542, lng: 124.6319 }); // Default to Cagayan de Oro coordinates
  }, []);

  const handleSOS = () => {
    alert('Emergency contacts notified with your current location!');
    // In a real app, this would send SMS/emails to emergency contacts
  };

  const renderAuthForm = () => {
    return (
      <div className="auth-modal">
        <div className="auth-container">
          <button className="close-auth" onClick={() => setAuthMode(null)}></button>
          <h2>{authMode === 'signin' ? 'Sign In' : 'Sign Up'}</h2>

          <form onSubmit={authMode === 'signin' ? handleSignIn : handleSignUp}>
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={authForm.email}
                onChange={handleAuthInputChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                name="password"
                value={authForm.password}
                onChange={handleAuthInputChange}
                required
                minLength="6"
              />
            </div>

            {authMode === 'signup' && (
              <div className="form-group">
                <label>Confirm Password</label>
                <input
                  type="password"
                  name="confirmPassword"
                  value={authForm.confirmPassword}
                  onChange={handleAuthInputChange}
                  required
                  minLength="6"
                />
              </div>
            )}

            <button type="submit" className="auth-submit">
              {authMode === 'signin' ? 'Sign In' : 'Sign Up'}
            </button>
          </form>

          <div className="auth-switch">
            {authMode === 'signin' ? (
              <p>Don't have an account? <button onClick={() => setAuthMode('signup')}>Sign Up</button></p>
            ) : (
              <p>Already have an account? <button onClick={() => setAuthMode('signin')}>Sign In</button></p>
            )}
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="e-quake-app">
      {/* Navigation */}
      <nav className="navbar">
        <div className="container">
          <a href="/" className="logo">E-QUAKE</a>
          <div className="nav-links">
            <button className={activeTab === 'alerts' ? 'active' : ''} onClick={() => setActiveTab('alerts')}>Alerts</button>
            <button className={activeTab === 'map' ? 'active' : ''} onClick={() => setActiveTab('map')}>Map</button>
            <button className={activeTab === 'preparedness' ? 'active' : ''} onClick={() => setActiveTab('preparedness')}>Preparedness</button>
            <button className={activeTab === 'resources' ? 'active' : ''} onClick={() => setActiveTab('resources')}>Resources</button>
            <button className="sos-btn" onClick={handleSOS}>SOS</button>
          </div>
          <button className="mobile-menu-btn"></button>
        </div>
      </nav>

      {/* Main Content */}
      <main className="container">
        {activeTab === 'alerts' && <AlertsTab earthquakes={earthquakes} userLocation={userLocation} />}
        {activeTab === 'map' && <MapTab />}
        {activeTab === 'preparedness' && <PreparednessTab />}
        {activeTab === 'resources' && <ResourcesTab />}
      </main>

      {/* Footer */}
      <footer className="footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-logo">
              <h3>E-QUAKE</h3>
              <p>Comprehensive Earthquake Preparedness Hub</p>
            </div>
            <div className="footer-links">
              <div>
                <h4>Quick Links</h4>
                <a href="#about">About</a>
                <a href="#contact">Contact</a>
                <a href="#privacy">Privacy Policy</a>
              </div>
              <div>
                <h4>Resources</h4>
                <a href="#phivolcs">PHIVOLCS</a>
                <a href="#ndrrmc">NDRRMC</a>
                <a href="#redcross">Red Cross</a>
              </div>
            </div>
          </div>
          <div className="footer-bottom">
            <p> 2025 University of Science and Technology of Southern Philippines</p>
            <p>IT323 - Application Development Project</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

// Tab Components
function AlertsTab({ earthquakes, userLocation }) {
  return (
    <section className="alerts-tab">
      <div className="alert-header">
        <h2>Recent Earthquakes</h2>
        <div className="alert-filter">
          <span>Filter by:</span>
          <select>
            <option>All Alerts</option>
            <option>Red Alerts</option>
            <option>Yellow Alerts</option>
            <option>Green Alerts</option>
          </select>
        </div>
      </div>

      <div className="earthquake-list">
        {earthquakes.map(quake => (
          <div key={quake.id} className={`quake-card ${quake.alert}`}>
            <div className="quake-magnitude">
              <span>{quake.magnitude}</span>
              <small>Magnitude</small>
            </div>
            <div className="quake-details">
              <h3>{quake.location}</h3>
              <p>Depth: {quake.depth}  {quake.time}</p>
              {userLocation && <p className="distance">Approx. 120km from you</p>}
            </div>
            <div className={`alert-level ${quake.alert}`}>
              {quake.alert.toUpperCase()} ALERT
            </div>
          </div>
        ))}
      </div>

      <div className="alert-settings">
        <h3>Alert Settings</h3>
        <div className="settings-grid">
          <div className="setting-card">
            <h4>Notification Preferences</h4>
            <div className="toggle-group">
              <label>
                <input type="checkbox" checked /> Push Notifications
              </label>
              <label>
                <input type="checkbox" checked /> Email Alerts
              </label>
              <label>
                <input type="checkbox" /> SMS Alerts
              </label>
            </div>
          </div>
          <div className="setting-card">
            <h4>Alert Sensitivity</h4>
            <select>
              <option>All earthquakes</option>
              <option>Magnitude 4.0+</option>
              <option selected>Magnitude 5.0+</option>
              <option>Magnitude 6.0+</option>
            </select>
          </div>
          <div className="setting-card">
            <h4>Location Settings</h4>
            <p>Current location: {userLocation ? "Detected" : "Not available"}</p>
            <button className="update-btn">Update Location</button>
          </div>
        </div>
      </div>
    </section>
  );
}

function MapTab() {
  return (
    <section className="map-tab">
      <h2>Earthquake Map</h2>
      <div className="map-container">
        {/* In a real app, this would be an interactive map component */}
        <div className="map-placeholder">
          <p>[Interactive Map Displaying Recent Earthquakes]</p>
          <div className="map-legend">
            <div><span className="legend-red"></span> Magnitude 6.0+</div>
            <div><span className="legend-yellow"></span> Magnitude 4.5-5.9</div>
            <div><span className="legend-green"></span> Magnitude &lt;4.5</div>
          </div>
        </div>
      </div>
      <div className="map-controls">
        <button className="map-btn">Refresh Data</button>
        <button className="map-btn">Find Shelters</button>
        <button className="map-btn">Evacuation Routes</button>
      </div>
    </section>
  );
}

function PreparednessTab() {
  const [activeGuide, setActiveGuide] = useState('before');

  return (
    <section className="preparedness-tab">
      <h2>Earthquake Preparedness</h2>

      <div className="guide-tabs">
        <button 
          className={activeGuide === 'before' ? 'active' : ''} 
          onClick={() => setActiveGuide('before')}
        >
          Before Earthquake
        </button>
        <button 
          className={activeGuide === 'during' ? 'active' : ''} 
          onClick={() => setActiveGuide('during')}
        >
          During Earthquake
        </button>
        <button 
          className={activeGuide === 'after' ? 'active' : ''} 
          onClick={() => setActiveGuide('after')}
        >
          After Earthquake
        </button>
      </div>

      <div className="guide-content">
        {activeGuide === 'before' && (
          <div>
            <h3>Before an Earthquake</h3>
            <ul>
              <li>Identify safe spots in each room (under sturdy furniture, against inside walls)</li>
              <li>Practice "Drop, Cover, and Hold On" drills</li>
              <li>Secure heavy items that could fall and cause injuries</li>
              <li>Prepare an emergency kit with food, water, and supplies</li>
              <li>Know how to turn off gas, water, and electricity</li>
            </ul>
          </div>
        )}
        {activeGuide === 'during' && (
          <div>
            <h3>During an Earthquake</h3>
            <ul>
              <li>Drop to your hands and knees to prevent being knocked over</li>
              <li>Cover your head and neck with your arms</li>
              <li>Hold on to any sturdy shelter until shaking stops</li>
              <li>If indoors, stay there - don't run outside</li>
              <li>If outdoors, move to an open area away from buildings</li>
            </ul>
          </div>
        )}
        {activeGuide === 'after' && (
          <div>
            <h3>After an Earthquake</h3>
            <ul>
              <li>Expect aftershocks and be prepared to Drop, Cover, and Hold On again</li>
              <li>Check yourself and others for injuries</li>
              <li>Look for and extinguish small fires</li>
              <li>Listen to official information sources for updates</li>
              <li>Use text messages instead of phone calls to communicate</li>
            </ul>
          </div>
        )}
      </div>

      <div className="emergency-kit">
        <h3>Emergency Kit Checklist</h3>
        <div className="checklist-grid">
          <label><input type="checkbox" /> Water (1 gallon per person per day)</label>
          <label><input type="checkbox" /> Non-perishable food</label>
          <label><input type="checkbox" /> First aid kit</label>
          <label><input type="checkbox" /> Flashlight + extra batteries</label>
          <label><input type="checkbox" /> Whistle</label>
          <label><input type="checkbox" /> Dust masks</label>
          <label><input type="checkbox" /> Medications</label>
          <label><input type="checkbox" /> Copies of important documents</label>
          <label><input type="checkbox" /> Cash in small bills</label>
          <label><input type="checkbox" /> Emergency blanket</label>
        </div>
        <button className="print-btn">Print Checklist</button>
      </div>
    </section>
  );
}

const ResourcesTab = () => {
  const [activeResource, setActiveResource] = useState(null);

  const resources = {
    basics: {
      title: "Earthquake Basics",
      content: (
        <div>
          {/* ... existing content ... */}
        </div>
      )
    },
    building: {
      title: "Building Safety Guide",
      content: (
        <div>
          {/* ... existing content ... */}
        </div>
      )
    },
    family: {
      title: "Family Emergency Plan",
      content: (
        <div>
          {/* ... existing content ... */}
        </div>
      )
    },
    quiz: {
      title: "Earthquake Preparedness Quiz",
      content: (
        <div className="quiz-container">
          {!quizSubmitted ? (
            <>
              <h3>Test Your Knowledge</h3>
              <p>Answer these questions to check your earthquake preparedness knowledge.</p>

              {currentQuizQuestions.map((q, index) => (
                <div key={q.id} className="quiz-question">
                  <p>{index + 1}. {q.question}</p>

                  {q.type === 'mcq' && q.options.map(opt => (
                    <label key={opt.id}>
                      <input 
                        type="radio" 
                        name={q.id} 
                        value={opt.id}
                        onChange={() => setQuizAnswers({
                          ...quizAnswers, 
                          [q.id]: opt.id
                        })}
                      />
                      {opt.text}
                    </label>
                  ))}

                  {q.type === 'checkbox' && q.options.map(opt => (
                    <label key={opt.id}>
                      <input 
                        type="checkbox" 
                        name={`${q.id}_${opt.id}`}
                        onChange={(e) => setQuizAnswers({
                          ...quizAnswers, 
                          [`${q.id}_${opt.id}`]: e.target.checked
                        })}
                      />
                      {opt.text}
                    </label>
                  ))}

                  {q.type === 'truefalse' && (
                    <>
                      <label>
                        <input 
                          type="radio" 
                          name={q.id} 
                          value="true"
                          onChange={() => setQuizAnswers({
                            ...quizAnswers, 
                            [q.id]: "true"
                          })}
                        />
                        True
                      </label>
                      <label>
                        <input 
                          type="radio" 
                          name={q.id} 
                          value="false"
                          onChange={() => setQuizAnswers({
                            ...quizAnswers, 
                            [q.id]: "false"
                          })}
                        />
                        False
                      </label>
                    </>
                  )}
                </div>
              ))}

              <button 
                className="quiz-submit"
                onClick={() => setQuizSubmitted(true)}
              >
                Submit Answers
              </button>
            </>
          ) : (
            <div className="quiz-results">
              <h3>Your Quiz Results</h3>

              {currentQuizQuestions.map((q, index) => {
                let userCorrect = false;
                let correctAnswer = '';

                if (q.type === 'mcq') {
                  const correctOption = q.options.find(opt => opt.correct);
                  userCorrect = quizAnswers[q.id] === correctOption.id;
                  correctAnswer = correctOption.text;
                } 
                else if (q.type === 'checkbox') {
                  const allCorrect = q.options
                    .filter(opt => opt.correct)
                    .every(opt => quizAnswers[`${q.id}_${opt.id}`]);
                  userCorrect = allCorrect;
                  correctAnswer = q.options.filter(opt => opt.correct).map(opt => opt.text).join(', ');
                } 
                else if (q.type === 'truefalse') {
                  userCorrect = quizAnswers[q.id] === q.correctAnswer;
                  correctAnswer = q.correctAnswer;
                }

                return (
                  <div key={q.id} className="result-item">
                    <p><strong>Question {index + 1}:</strong> {q.question}</p>
                    <p className={userCorrect ? "correct" : "incorrect"}>
                      {userCorrect ? " Correct!" : " Incorrect"}
                    </p>
                    <p className="explanation">
                      <strong>Correct answer:</strong> {correctAnswer}<br />
                      <strong>Explanation:</strong> {q.explanation}
                    </p>
                  </div>
                );
              })}

              <button 
                className="quiz-retake"
                onClick={generateNewQuiz}
              >
                Take New Quiz
              </button>
            </div>
          )}
        </div>
      )
    }
  };
  return (
    <section className="resources-tab">
      <h2>Educational Resources</h2>

      {activeResource ? (
        <div className="resource-viewer">
          <button 
            className="back-button"
            onClick={() => setActiveResource(null)}
          >
             Back to Resources
          </button>
          <h3>{resources[activeResource].title}</h3>
          <div className="resource-content">
            {resources[activeResource].content}
          </div>
        </div>
      ) : (
        <>
          <div className="resource-cards">
            <div 
              className="resource-card"
              onClick={() => setActiveResource('basics')}
            >
              <h3>Earthquake Basics</h3>
              <p>Learn about what causes earthquakes and how they're measured.</p>
              <div className="resource-link">Read Article </div>
            </div>
            <div 
              className="resource-card"
              onClick={() => setActiveResource('building')}
            >
              <h3>Building Safety</h3>
              <p>How to earthquake-proof your home and identify structural risks.</p>
              <div className="resource-link">View Guide </div>
            </div>
            <div 
              className="resource-card"
              onClick={() => setActiveResource('family')}
            >
              <h3>Family Plan</h3>
              <p>Create an earthquake preparedness plan for your family.</p>
              <div className="resource-link">Download Template </div>
            </div>
            <div 
              className="resource-card"
              onClick={() => setActiveResource('quiz')}
            >
              <h3>Interactive Quiz</h3>
              <p>Test your earthquake knowledge with our preparedness quiz.</p>
              <div className="resource-link">Take Quiz </div>
            </div>
          </div>

          <div className="official-resources">
            <h3>Official Resources</h3>
            <div className="official-links">
              <a href="https://www.phivolcs.dost.gov.ph" target="_blank" rel="noopener noreferrer">
                PHIVOLCS - Philippine Institute of Volcanology and Seismology
              </a>
              <a href="https://ndrrmc.gov.ph" target="_blank" rel="noopener noreferrer">
                NDRRMC - National Disaster Risk Reduction and Management Council
              </a>
              <a href="https://redcross.org.ph" target="_blank" rel="noopener noreferrer">
                Philippine Red Cross
              </a>
            </div>
          </div>
        </>
      )}
    </section>

);
}


export default App;
