import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import heroVideo from '../assets/hero-intro.mp4';
import quakeSound from '../assets/quake-sound.mp3';

const HeroSection = ({ scrollRef }) => {
  const overlayRef = useRef();
  const videoRef = useRef();
  const audioRef = useRef();

  useEffect(() => {
    const quakeTimeline = gsap.timeline({ paused: true });

    quakeTimeline.to(".hero", {
      x: -10,
      duration: 0.1,
      repeat: 5,
      yoyo: true,
      ease: "power1.inOut"
    });

    videoRef.current.addEventListener('play', () => {
      audioRef.current.play();
      quakeTimeline.restart();
    });
  }, []);

  const handleEnter = () => {
    scrollRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <section className="hero">
      <video ref={videoRef} autoPlay loop muted className="background-video">
        <source src={heroVideo} type="video/mp4" />
      </video>
      <audio ref={audioRef} src={quakeSound} preload="auto" />
      <div className="overlay" ref={overlayRef}>
        <h1>EARTHQUAKE ZONE</h1>
        <h2>Prepare. React. Survive.</h2>
        <button className="enter-button" onClick={handleEnter}>
          Enter Experience
        </button>
      </div>
    </section>
  );
};

export default HeroSection;
